import React from 'react'
import {NavLink} from 'react-router-dom'

import TagList from 'components/tagList'
import AddToFavorites from 'components/addToFavorites'

const Feed = ({articles}) => {
    return (
        <div>
            {articles.map((article, i) => (
                <div className='article-preview' key={i}>
                    <div className='article-meta'>
                        <NavLink to={`/profile/${article.author.username}`}>
                            <img src={article.author.image} alt=''/>
                        </NavLink>
                        <div className='info'>
                            <NavLink to={`/profile/${article.author.username}`} className='author'>
                                {article.author.username}
                            </NavLink>
                            <span className='date'>{article.createdAt}</span>
                        </div>
                        <div className='pull-xs-right'>
                            <AddToFavorites isFavorited={article.favorited} favoritesCount={article.favoritesCount} articleSlug={article.slug}/>
                        </div>
                    </div>
                    <NavLink to={`/articles/${article.slug}`} className='preview-link'>
                        <h2>{article.title}</h2>
                        <p>{article.description}</p>
                        <span>Read more...</span>
                        <TagList tags={article.tagList} />
                    </NavLink>
                </div>
            ))}
        </div>
    )
}

export default Feed