import {Switch, Route} from 'react-router-dom'
import React from 'react'

import GlobalFeed from 'pages/globalFeed'
import Article from 'pages/article'

//Switch определяет на каком я роуте и какой роут необходим, если написать без switch то все роуты вместе выведутся на странице
//exact рендерит роут только если есть точное совпадение с path
export default () => {
    return (
        <Switch>
            <Route exact path='/' component={GlobalFeed}/>
            <Route path='/articles/:slug' component={Article}/>
        </Switch>
    )
}